import React, { useState } from 'react'
import styled from 'styled-components'

function Edit() {
    const [title, setTitle] = useState("");
    const [gameA, setGameA] = useState("");
    const [gameB, setGameB] = useState("");
    console.log("title ->", title)
    console.log("gameA ->", gameA)
    console.log("gameB ->", gameB)

    // title 변경 감지하는 함수
    const handleTitleChange = (e) => {
        setTitle(e.target.value)
    }
    //  gameA 변경 감지하는 함수
    const handleGameAchange = (e) => {
        setGameA(e.target.value)
    }
    //  gameB 변경 감지하는 함수
    const handleGameBchange = (e) => {
        setGameB(e.target.value)
    }


    return (
    <form>
        <div>
            나만의 밸런스 게임을 등록해주세요!
            <StInputWrapper>
                <label />
                제목 <input 
                type="text" placeholder='8글자 내로 제목을 지어주세요.' maxLength="10" minLength="1"
                value={title}
                onChange={handleTitleChange}
                />
                <label/>게임a
                <input 
                type="text" placeholder='A의견을 적어주세요' minLength="1"
                value={gameA}
                onChange={handleGameAchange}
                />
                <label/>게임b
                <input 
                type="text" placeholder='A의견을 적어주세요'minLength="1"
                value={gameB}
                onChange={handleGameBchange}
                />
            </StInputWrapper>
            <div>
                <button>등록</button>&nbsp;
                <button>취소</button>
            </div>

        </div>
    </form>
    )
}

export default Edit
const StFormWrapper = styled.form`

`
const StInputWrapper = styled.div`
    display: flex;
    flex-direction: column;
    margin: 14pt;
    gap: 5pt;
`
