import axios from 'axios';
import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components'

//  등록하면 cardlist에 card 추가 되도록.

function Edit() {
    const navigate = useNavigate();

    const [title, setTitle] = useState("");
    const [optionA, setOptionA] = useState("");
    const [optionB, setOptionB] = useState("");
    // console.log("title ->", title)
    // console.log("gameA ->", gameA)
    // console.log("gameB ->", gameB)

    // title 변경 감지하는 함수
    const handleTitleChange = (e) => {
        setTitle(e.target.value)
    }
    //  gameA 변경 감지하는 함수
    const handleGameAchange = (e) => {
        setOptionA(e.target.value)
    }
    //  gameB 변경 감지하는 함수
    const handleGameBchange = (e) => {
        setOptionB(e.target.value)
    }

    //추가 함수
    const onSubmitHandler = async (id) =>  {
        axios.post(`http://localhost:4000/cards`, {title, optionA, optionB})
        // fetchCards();
    }


    return (
    <form>
        <div>
            나만의 밸런스 게임을 등록해주세요!
            <StInputWrapper onSubmit={(e) => {
                e.preventDefault();
                onSubmitHandler();
            }}>
                <label />
                제목 <input 
                type="text" placeholder='8글자 내로 제목을 지어주세요.' maxLength="10" minLength="1"
                value={title}
                onChange={handleTitleChange}
                />
                <label/>optionA
                <input 
                type="text" placeholder='A의견을 적어주세요' minLength="1"
                value={optionA}
                onChange={handleGameAchange}
                />
                <label/>optionB
                <input 
                type="text" placeholder='A의견을 적어주세요'minLength="1"
                value={optionB}
                onChange={handleGameBchange}
                />
            </StInputWrapper>
            <div>
                <button>등록</button>&nbsp;
                <button
                onClick={navigate("/games")}
                >취소</button>
            </div>

        </div>
    </form>
    )
}

export default Edit
const StFormWrapper = styled.form`

`
const StInputWrapper = styled.div`
    display: flex;
    flex-direction: column;
    margin: 14pt;
    gap: 5pt;
`
