import axios from 'axios';
import React, { useEffect, useState } from 'react'
import styled from 'styled-components';

function CardList() {
    const [cards, setCards] = useState([]);
    console.log("cards ->", cards)
    // 조회함수
    const fetchCards = async () => {
        const { data } = await axios.get('http://localhost:4000/cards');
        setCards(data)
    };
    // 랜더링 되자마자 바로 리스트 조회함수 불러오기(by useEffect).
    useEffect(()=>{
        fetchCards()
    }, []);

    // 

    return (
    <>
        <div>
        카드리스트 여기서는 axios get으로 카드리스트(지금은dbjson)가져와서 거기다 맵으로 div에 뿌려준다
        </div>
        <div>
            {
                cards.map((cards) => {
                    return (
                        <StCardList key={cards.id}>
                            {cards.title} <br />
                            {cards.optionA} vs {cards.optionB}
                        </StCardList>
                    )
                })
            }
        </div>
    </>


    )
}

export default CardList
const StCardList = styled.div`
    width: 100px;
    height: 100px;
    background-color: green;
    margin-bottom: 10px;
`
